class Tagger:
    def __init__(self, language=None):
        self.language = language

    def get_text_index_string(self, text):
        # Return plain text as fallback
        return text


class PosLemmaTagger(Tagger):
    def __init__(self, language=None):
        super().__init__(language)

    def get_text_index_string(self, text):
        # Same as Tagger (no NLP, no spaCy)
        return text

